#!/usr/bin/python
import scrapy
import sys
import time
import urllib
import urllib2

from scrapy.selector import Selector
from scrapy.http import HtmlResponse

url = "https://backchannel.com/a-teenagers-view-on-social-media-1df945c09ac6?source=false---------1"

url_handler = urllib2.build_opener()
urllib2.install_opener(url_handler)
handle = url_handler.open(url)
response = handle.read()
handle.close()

html_response = HtmlResponse(url, body=response)
hxs = Selector(html_response)
desc = hxs.css('div.layoutSingleColumn').extract()[0]
try:
	ts = time.time()
	html_name = 'test/socialmedia' + str(ts) + '.html'
	file = open(html_name, 'w+')
	file.write(desc)
	file.close()
except AttributeError:
	print(AttributeError)
	sys.exit(0)

